use dotenv::dotenv; use crate::config::SupabaseConfig; #[tokio::main] async fn main() -> Result<(), Box<dyn std::error::Error>> { dotenv().ok(); let config = SupabaseConfig::new()?; let response = config.client.from("information_schema.tables").select("table_name").eq("table_schema", "public").execute().await?; println!("Status: {}", response.status()); if let Ok(text) = response.text().await { println!("Tables: {}", text); } Ok(()) }
